changeSet.sql:
insert into REQUIREMENT_AUDIT_EVENT (requirement_id, event_date, author) 
select r.rln_id, rln.created_on, rln.created_by  
from REQUIREMENT_LIBRARY_NODE as rln, REQUIREMENT as r 
where r.rln_id=rln.rln_id;		

insert into REQUIREMENT_CREATION (event_id) select event_id from REQUIREMENT_AUDIT_EVENT;
